# The main function that controls the game
async def main () :
  
  state = "start"
  level = Level("Megalovania", "images/HD-wallpaper-sans-undertale (1).png", "Toby Fox", 5, 3)

  """INITIATE THE SCREENS"""
  start = start_screen()
  start.running = True

  menu = menu_screen()
  menu.running = False

  game = game_screen(level, MEGALOVANIA_DATA, ["A", "S", "D", "F", "J", "K", "L", ";"], False)
  game.running = False

  creator = creator_screen()
  creator.running = False


  """MAIN GAME LOOP"""
  while True :

 
    """USER INPUT"""
    events = []
    for event in pygame.event.get() :
      events.append(event)
    

    """ACTIVATE SCREENS"""
    if start.running:
      state = start.run(events)
    
    if menu.running:
      state, level = menu.run(events)
    
    if game.running:
      state = game.run(events)
    
    if creator.running: 
      state, level, data, keys = creator.run(events)

    
    #Init State
    if state == "game_init":
      if level.name == "Megalovania":
        data = MEGALOVANIA_DATA
        keys = ["A", "S", "D", "F", "J", "K", "L", ";"]
      elif level.name == "Field of Hopes an...":
        data = FIELD_DATA
        keys = ["A", "S", "D", "F", "J", "K", "L", ";"]
      elif level.name == "Hunger":
        data = HUNGER_DATA
        keys = ["A", "S", "D", "F", "J", "K", "L", ";"]
      game = game_screen(level, data, keys, False)
      state = "game"
    elif state == "menu_init":
      menu = menu_screen()
      state = "menu"
    elif state == "creator_init":
      creator = creator_screen()
      state = "creator"
    elif state == "test_init":
      game = game_screen(level, data, keys, True)
      state = "game"
      

    #State
    if state == "start":
      start.running = True
      menu.running = False
      game.running = False
      creator.running = False
    elif state == "menu":
      start.running = False
      menu.running = True
      game.running = False
      creator.running = False
    elif state == "game":
      start.running = False
      menu.running = False
      game.running = True
      creator.running = False
    elif state == "creator":
      start.running = False
      menu.running = False
      game.running = False
      creator.running = True



 
    """UPDATE and FPS"""
    pygame.display.update()
    fpsClock.tick(FPS)

    await asyncio.sleep(0)

asyncio.run(main())